import React from 'react';
import Restaurant from './Restaurant';

const RestaurantList =({restaurants})=>{

    const renderedRestaurants = restaurants.map((restaurant)=>{
        return <Restaurant restaurant={restaurant}/>;
    });
    if(renderedRestaurants.length>0) {
        return(
            <div style={{backgroundColor:'lightblue', paddingTop: '10px', paddingBottom:'10px'}}>
                <h4 style={{textAlign: 'center'}}>TOP THREE RESTAURANT</h4>
                <div className="ui segment relaxed divided list">{ renderedRestaurants }</div>
            </div>

        ); 
    }
    return( 
        <div className="ui relaxed divided list">{ renderedRestaurants }</div>
    );
}

export default RestaurantList;
