import React from 'react';
const Weather = ({ weather }) => {
    if(!weather) {
        return <div>Enter Valid Zip Code</div>
    }
    console.log(weather);

    var temp;
    var cityLabel=null;
    var tempLabel=null;
    for(let x in weather.main){
        if(x==='temp'){
            temp = weather.main[x] + ' ºF'
            tempLabel="Current temperature: "
            cityLabel="Your city: "
        }
    }
    console.log(weather.weather[0])
    return( 
        <div className="item">
            <div className="content">
                <div>
                <label style={{fontWeight:'bold'}}>{cityLabel}</label>{weather.name}
                </div>
                <div><label style={{fontWeight:'bold'}}>{tempLabel}</label>{temp}</div> 
            </div>
        </div>
    );
}

export default Weather;
