import React from 'react';
import WSearch from './WSearch';
import Weather from './Weather';

class App extends React.Component {
    state = { weatherInfo: null}


    onSearchSubmit= async(zip)=> {
        console.log(zip)
        const Api_Key = '46fbccf86507b640e7eefa10c686dd85';
        const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?zip=${zip},us&units=imperial&appid=${Api_Key}`);

        const response = await api_call.json();

        this.setState( {weatherInfo: response} )
        console.log(response.text);
        console.log(response.name);
        console.log(response.main.temp + 'ºF');
        console.log(response.weather[0].main);

    }

    render() {
        return(
            <div className="ui container" style={{marginTop:'10px'}}>
               <WSearch onSubmit={this.onSearchSubmit} /> 
               <Weather />
            </div>
        );
    }
}


export default App;
