import React from 'react';
import WSearch from './WSearch';
import Weather from './Weather';

class App extends React.Component {
    state = { weatherInfo: []}


    onSearchSubmit= async(zip)=> {
        console.log(zip)
        const WApi_Key = '46fbccf86507b640e7eefa10c686dd85';
        const Wapi_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?zip=${zip},us&units=imperial&appid=${WApi_Key}`);

        const response_weather = await Wapi_call.json();

        this.setState( {weatherInfo: response_weather} )
        //console.log(response_weather.name);
        //console.log(response_weather.main.temp + 'ºF');
        //console.log(response_weather.weather[0].main);

        const PApi_Key = 'AIzaSyDzn5hymTUP7biD9AqlX1vZEYzRuuqMzwg';
        const PApi_call = await fetch(`https://maps.googleapis.com/maps/api/place/textsearch/json?query=10583&key=${PApi_Key}`)
        //fetch(`https://maps.googleapis.com/maps/api/place/textsearch/json?query=restaurants,${zip}&key=${PApi_Key}`);
        const response_restaurant = await PApi_call.json();
        console.log(response_restaurant)


    }

    render() {
        return(
            <div className="ui container" style={{marginTop:'10px'}}>
               <WSearch onSubmit={this.onSearchSubmit} /> 
               <Weather />
            </div>
        );
    }
}


export default App;
